    def csv_to_dictionary_countries
        read_csv
        csv = csv.each_with_index.map { |row, i|  [row[0].downcase.capitalize, [row[1]]] if i != 0 }.compact
        convert_to_hash
    end

    def csv_to_dictionary_currencies
        read_csv
        csv = csv.each_with_index.map { |row, i|  [row[1],[row[0].downcase.capitalize]] if i != 0 }.compact
        convert_to_hash
    end

    def read_csv
        csv = CSV.read("db/countries_and_currencies.csv")
    end

    def convert_to_hash
        csv = csv.to_h 
        File.write('db/dictionary.rb', csv)
    end
